package bd.edu.seu.springbootdemo.repository;import bd.edu.seu.springbootdemo.model.Student;import org.springframework.data.repository.CrudRepository;import java.time.LocalDate;import java.util.List;public interface StudentRepository extends CrudRepository<Student,Long> {    public List<Student> findByName(String name);    public List<Student> findByNameContaining(String partial);    List<Student> findAllByLocalDateAfter(LocalDate localDate);    public void deleteById(Long id);    public List<Student> findAllByLocalDateBetween(LocalDate start,LocalDate end);    public List<Student> findAllByCgpaAfter(double start);}